import { createSelector } from 'reselect';

const COLLECTION_ID_MAP = {
    hats: 1,
    sneakers: 2,
    jackets: 3,
    womens: 4,
    mens: 5
}

const ShopDataSelector = state => state.shop;

export const getCollectionData = createSelector(
    [ShopDataSelector],
    (shop) => shop.collections
)
