import React from 'react'
import { connect } from 'react-redux';
import { toggleCart } from '../../../redux/cart/cart.actions.js';
import './cart-icon.styles.scss';
import { ReactComponent as ShoppingIcon } from '../../../assets/shopping-bag.svg';


const CartIcon = ({ toggleCart, itemNumber, cartItems }) =>  (
    <div className='cart-icon' onClick={toggleCart} >
        <ShoppingIcon className='shopping-icon' />
            { cartItems 
            ?  cartItems.map((item, index) => 
            <span className='item-count' key={index}>
                {item.quantity}
            </span> )
            :  null 
            }
    </div>
)



const dispatchStateToProps = dispatch => ({
    toggleCart: () => dispatch(toggleCart())
})

const mapStateToProps = ({ cart: { itemNumber, cartItems} }) => ({
    itemNumber, cartItems
})


export default connect(mapStateToProps, dispatchStateToProps)(CartIcon);
