import React, { Component } from 'react';
import { Route } from "react-router-dom";
import { createStructuredSelector } from 'reselect';

import { connect } from 'react-redux';
import { fetchCollectionsStartAsync } from '../../redux/shop/shop.actions.js';
import { selectShop,
         SelectionCollectionsLoaded } from '../../redux/shop/shop.selector.js';

import CollectionPage from '../collection/collection.component.jsx';
import CollectionOverview from '../../components/collections-overview/collections-overview.component.jsx';
import WithSpinner from '../../components/spinner/spinner.components.jsx';

const CollectionsOverviewWithSpinner = WithSpinner(CollectionOverview);
const CollectionsPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends Component {

    componentDidMount() {
        const { fetchCollectionsStartAsync } = this.props;
        fetchCollectionsStartAsync()
    }

    render() {
    const { match, shop, collectionsLoading } = this.props;

    return(
        <div className='shop-page'>
            <Route exact path={`${match.path}`} render={ (props) => 
                <CollectionsOverviewWithSpinner isLoading={shop.isFetching} {...props}/>} />
            <Route path={`${match.path}/:collectionId`} render={(props) => 
                <CollectionsPageWithSpinner isLoading={shop.isFetching} {...collectionsLoading} />} />
        </div>
        )
    }
}

const mapStateToProps = createStructuredSelector({
    shop: selectShop,
    collectionsLoading: SelectionCollectionsLoaded
})

const mapDispatchToPros = dispatch => ({
    fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
})

export default connect(mapStateToProps, mapDispatchToPros)(ShopPage);
