import React from 'react'
import { connect } from 'react-redux';
import { toggleCart } from '../../../redux/cart/cart.actions.js';
import './cart-icon.styles.scss';
import { ReactComponent as ShoppingIcon } from '../../../assets/shopping-bag.svg';


const CartIcon = () => {
    const { toggleCart } = this.props;
    return (
    <div className='cart-icon'>
        <ShoppingIcon className='shopping-icon' onClick={toggleCart}/>
        <span className='item-count'>
            0
        </span>
    </div>
)}


const dispatchStateToProps = dispatch => ({
    toggleCart: event => dispatch(toggleCart(event))
})


export default connect(null, dispatchStateToProps)(CartIcon);
